Insert into SIAMI.KRITERIA (ID,STANDAR_ID,KODE,KRITERIA,KET_NILAI,CATATAN,CREATED_AT,UPDATED_AT,TAHUN) values ('63','67','K9','Kesesuaian bidang kerja lulusan saat mendapatkan pekerjaan pertama dalam 3 tahun dengan responden mulai dari TS-4 hingga TS-2 (PBS). &#13;&#10;Keterangan: Jumlah responden minimal 65 orang untuk Prodi D3, 80 orang untuk Prodi D4, 15 orang untuk Prodi S2 ','0 : PBS &amp;#60; 15% atau tidak memenuhi persyaratan minimal jumlah responden&amp;#13;&amp;#10;1 : 15% &le; PBS &amp;#60; 30%&amp;#13;&amp;#10;2 : 30% &le; PBS &amp;#60; 45%&amp;#13;&amp;#10;3 : 45% &le; PBS &amp;#60; 50%&amp;#13;&amp;#10;4 : PBS &ge; 50%','Menunjukkan data pendukung',to_timestamp('06-07-2022 11.05.57,721000000','DD-MM-RRRR HH24.MI.SSXFF'),to_timestamp('06-07-2022 11.05.57,721000000','DD-MM-RRRR HH24.MI.SSXFF'),'2022');
Insert into SIAMI.KRITERIA (ID,STANDAR_ID,KODE,KRITERIA,KET_NILAI,CATATAN,CREATED_AT,UPDATED_AT,TAHUN) values ('64','67','K10','Publikasi ilmiah mahasiswa yang dihasilkan secara  mandiri atau bersama DTPS dengan judul  yang relevan dengan bidang program studi &#13;&#10;Keterangan:&#13;&#10;RL = ((NA1 + NB1 + NC1) / NM) x 100% , RN = ((NA2 + NA3 + NB2 + NC2) / NM) x 100% , RI = ((NA4 + NB3 + NC3) / NM) x 100%&#13;&#10;Faktor: a = 1% , b = 10% , c = 50% (untuk Prodi D4);  a = 2% , b = 20% , c = 70% (untuk Prodi S2 Terapan); Tidak ada butir pertanyaan ini untuk D3&#13;&#10;NA1 = Jumlah publikasi mahasiswa di jurnal nasional tidak terakreditasi.&#13;&#10;NA2 = Jumlah publikasi mahasiswa di jurnal nasional terakreditasi.&#13;&#10;NA3 = Jumlah publikasi mahasiswa di jurnal internasional.&#13;&#10;NA4 = Jumlah publikasi mahasiswa di jurnal internasional bereputasi.&#13;&#10;NB1 = Jumlah publikasi mahasiswa di seminar wilayah/lokal/PT.&#13;&#10;NB2 = Jumlah publikasi mahasiswa di seminar nasional.&#13;&#10;NB3 = Jumlah publikasi mahasiswa di seminar internasional.&#13;&#10;NC1 = Jumlah pagelaran/pameran/presentasi mahasiswa dalam forum di tingkat wilayah.&#13;&#10;NC2 = Jumlah pagelaran/pameran/presentasi mahasiswa dalam forum di tingkat nasional.&#13;&#10;NC3 = Jumlah pagelaran/pameran/presentasi mahasiswa dalam forum di tingkat internasional.&#13;&#10;NM = Jumlah mahasiswa pada saat TS','0 - 1 : &amp;#13;&amp;#10;Jika RI = 0 dan RN = 0 dan RL &ge; c , maka Skor = 2&amp;#13;&amp;#10;Jika RI = 0 dan RN = 0 dan RL &amp;#60; c , maka Skor = (2 x RL) / c&amp;#13;&amp;#10;&amp;#13;&amp;#10;2 - 3 :&amp;#13;&amp;#10;Jika RI &amp;#60; a dan RN &ge; b, maka Skor = 3 + (RI / a)&amp;#13;&amp;#10;Jika 0 &amp;#60; RI &amp;#60; a dan 0 &amp;#60; RN &amp;#60; b , maka Skor = 2 + (2 x (RI/a)) + (RN/b) - ((RI x RN)/(a x b))&amp;#13;&amp;#10;&amp;#13;&amp;#10;4 : RI &ge; a','Menunjukkan data pendukung',to_timestamp('06-07-2022 11.07.45,998000000','DD-MM-RRRR HH24.MI.SSXFF'),to_timestamp('06-07-2022 11.07.45,998000000','DD-MM-RRRR HH24.MI.SSXFF'),'2022');
